const addMessage = 'ADD-MESSAGE'
const updateMessage = 'UPDATE-MESSAGE'

let initialState = {
    messageData : [
        { id: 0, message: 'Hello' },
        { id: 1, message: 'How are you' },
        { id: 2, message: 'Yo' }
    ],
    updateMessage: '',

    dialogsData : [
        { id: 0, name: "Sergey" },
        { id: 1, name: "Andrey" },
        { id: 2, name: "Grisha" },
        { id: 3, name: "Masha" },
        { id: 4, name: "Dasha" }
    ]
}

const dialogsReducer = (state=initialState, active) => {
    switch (active.type) {
        case addMessage:
            let newMessage = {
                id: 3,
                message: state.updateMessage,}
            state.messageData.push(newMessage);
            state.updateMessage = ('');
            return state
        case updateMessage:
            state.updateMessage = active.message;
            return state
        default:
            return state
}}

export const addMessageCreate = () => ({type: addMessage})

export const updateMessageCreate = (text) => ({
    type: updateMessage, message: text})

export default dialogsReducer
