        this.setState( {
            editMode: true
        })}

    deActivateEditMode = () => {
        this.setState( {
            editMode: false,    
        })
    }

    render () {
        return (
            <div>
                {!this.state.editMode &&
                <div>
                    <span onDoubleClick={this.activateEditMode}>{this.props.userStatus}</span>
                </div>}
                {this.state.editMode &&
                <div>
                    <input onChange={this.updateMyStatus}  autoFocus={true} 
                        onBlur={this.deActivateEditMode} value={this.props.userStatus}/>
                </div>}
            </div>
        )
    }
}

