export const requiredFields = (value) => {
    if (value) return undefined
    return "Field is required";
}

export const maxLengthCreator = (maxLength) => { 
    maxLength = (value) => {
    if (value.length > maxLength) return "Max length is" + maxLength + "symbols"
    return undefined
}}
