const set_auth_user = 'set-auth-user'


let initialState = {
    userId: null,
    login: null,
    email: null
}

const authReducer = (state=initialState, action) => {
    switch (action.type) {
        case addMessage: 
            let newMessage = {
                id: 3,
                message: state.updateMessage,};
            return {...state,
                messageData: [...state.messageData, newMessage],
                updateMessage: ''}

        case updateMessage: 
            return {...state,
                updateMessage: action.message}
        default:
            return state
}}

export const addMessageCreate = () => ({type: addMessage})

export const updateMessageCreate = (text) => ({
    type: updateMessage, message: text})

export default dialogsReducer
