import profileReducer from "./profileReducer"
const addMessage = 'ADD-MESSAGE'
const updateMessage = 'UPDATE-MESSAGE'
const addPost = 'ADD-POST'
const updatePost = 'UPDATE-POST'

let store = {
    _state: {
        profilePage: {
            postData: [
                { id: 1, message: "Hi, how are you?", like: 5 },
                { id: 2, message: "It's my first post", like: 6 },
                { id: 3, message: "and my second post", like: 10 }
            ],
            newPostText: ''
        },
        dialogsPage: {
            messageData : [
                { id: 0, message: 'Hello' },
                { id: 1, message: 'How are you' },
                { id: 2, message: 'Yo' }
            ],
            updateMessage: '',

            dialogsData : [
                { id: 0, name: "Sergey" },
                { id: 1, name: "Andrey" },
                { id: 2, name: "Grisha" },
                { id: 3, name: "Masha" },
                { id: 4, name: "Dasha" }
            ]
        },
        navPage: {
            friendsData: [
                {id: 0, name: 'Andrew', img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRXpnFKklZBeoDxYhmM63xKCzvnAwhfckERUmrIsYvmHg&s'},
                {id: 1, name: 'Sasha', img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT5JXIIGMkVpEvAVwnzWCWeQg91CxY8YG1p-G1x78E5&s'},
                {id: 2, name: 'Pasha', img: 'https://avatars.mds.yandex.net/get-kinopoisk-image/4303601/85c9204f-169c-46fd-8ca7-7473134c450b/1920x'}
            ]
        }  
    },
    _callSubscriber() {
        console.log('state changed')
    },
    getState() {
        return this._state
    },
    subscriber(observer) {
        this._callSubscriber = observer;
        },
    _updateNewPost (updateData) {
            this._state.profilePage.newPostText = updateData;
            this._callSubscriber(); 
            },
    _updateNewMessage (message) {

    },
    dispatch(active) {
        if (active.type === addPost) {
            let newPost = {
                id: 4,
                message: this._state.profilePage.newPostText,
                like: 0
                    }
                this._state.profilePage.postData.push(newPost);
                this._callSubscriber();
                this._state.profilePage.newPostText = ('');   
        } else if (active.type === updatePost) {
                this._state.profilePage.newPostText = active.updateData;
                this._callSubscriber(); 
        } else if (active.type === addMessage) {
            let newMessage = {
                id: 3,
                message: this._state.dialogsPage.updateMessage,
            }
            this._state.dialogsPage.messageData.push(newMessage);
            this._callSubscriber();
            this._state.dialogsPage.updateMessage = ('');
        } else if (active.type === updateMessage) {
            this._state.dialogsPage.updateMessage = message;
        this._callSubscriber();
        }
    }
}

export const addMessageCreate = () => ({type: addMessage})

export const updateMessageCreate = (text) => ({
    type: updateMessage, message: text})

export const addPostCreate = () => ({type: addPost})
export const updatePostCreate = (text) => ({
    type: updatePost, updateData: text})

export default store
