const set_auth_user = 'set-auth-user'

let initialState = {
    userId: null,
    email: null,
    login: null,
    isAuth: false
    }

const authReducer = (state=initialState, action) => {
    switch (action.type) {
        case set_auth_user: 
        debugger
            return {
                ...state,
                ...action.authData,
                isAuth: true
            }
        default:
            return state
}}

export const setAuthData = (userId, email, login) => ({
    type: set_auth_user, authData:{userId, email, login}})

export default authReducer
