import React from 'react'
import { follow, unfollow, setUsers, setPage, setTotal} from '../../Redux/usersReducer'
import Users from './Users'
import {connect} from 'react-redux'
import {getUsers} from '../../api/usersAPI'
import axios from 'axios'


class UsersAPI extends React.Component {

    componentDidMount() {
        axios.get('https://social-network.samuraijs.com/api/1.0/users',
    {params: {page: this.props.currentPage, count: this.props.pageSize}}).then(response => {
                this.props.setUsers(response.data.items);
                this.props.setTotal(response.data.totalCount)
        })    
}
    changePages = (p) => {
        this.props.setPage(p);
        getUsers(this.props.currentPage).then(response => {
        this.props.setUsers(response.data.items)
        })}


    render() {
        return (
        <div>
            <Users totalCount={this.props.totalCount}
                        pageSize={this.props.pageSize}
                        setPage={this.props.setPage}
                        currentPage={this.props.currentPage}
                        changePages={this.changePages}
                        usersData={this.props.usersData}
                        follow={this.props.follow}
                        unfollow={this.props.unfollow}/>
        </div>)
    }}

let mapStateToProps = (state) => {
    return {
        usersData: state.usersPage.usersData,
        totalCount: state.usersPage.totalCount,
        pageSize: state.usersPage.pageSize,
        currentPage: state.usersPage.currentPage,    
    }}

const UsersContainer = connect(mapStateToProps,{
    follow, unfollow, setUsers, setPage, setTotal})(UsersAPI);

export default UsersContainer
