const follow = 'FOLLOW'
const unfollow = 'UNFOLLOW'
const setUsers = 'set-users'

let initialState = {
    friendsData : [

    ]
}

const friendsReducer = (state=initialState, action) => {
    switch (action.type) {
        case follow:
            return {...state,
                friendsData: state.friendsData.map(f => {
                    if (f.id === action.userId) {
                        return {...f, followed: true}
                    }
                    return f
                })}
        case unfollow:
            return {...state,
                friendsData: state.friendsData.map(f => {
                    if (f.id === action.userId) {
                        return {...f, followed: false}
                    }
                    return f
                })} 
        case setUsers:
            return {...state,
            friendsData: [...state.friendsData, ...action.friendsData]}      
        default:
            return state
}}

export const changeToFollow = (userId) => ({type: follow, userId})
export const changeToUnfollow = (userId) => ({type: unfollow, userId})
export const setUsersForm = (friends) => ({type: setUsers, friends})

export default friendsReducer
