import React from 'react'
import s from './Dialogs.module.css'
import DialogsItem from './DialogsItem/DialogsItem'
import DialogsMessage from './DialogMessage/DialogsMessage'
import {reduxForm, Field} from 'redux-form'


const DialogsForm = (props) => {

    return(
        <form className={s.messages} onSubmit={props.handleSubmit}>
            {messageItems   }
            <div>
                <Field placeholder='Dialog' name={"Dialog"} component={"input"} onChange={onCheckUpdate} ref={newMessageEl} value={props.updateMessage}/>
            </div>
            <div>
                <button onClick={addMessage}>New message</button>
            </div>
        </form>
    )
}

const DialogsReduxForm = reduxForm({form:'Dialogs'})(DialogsForm)

const Dialogs = (props) => {
    const onSubmit = (formData) => {
        console.log(formData)
    }

    let dialogsItems = props.dialogsData
    .map(d => <DialogsItem name={d.name} id={d.id} /> )

    let messageItems = props.messageData
        .map(m => <DialogsMessage message={m.message}/>)

    let newMessageEl = React.createRef();

    let addMessage = () => {
        props.addMessage()
    }

    let onCheckUpdate = () => {
        let text = newMessageEl.current.value;
        props.newMessage(text)

    }
    return (

        <div className={s.dialogs} >
        <div className={s.dialogsItems}>
            {dialogsItems}
        </div>

        </div>

    )
}

export default Dialogs
