const addPost = 'ADD-POST'
const updatePost = 'UPDATE-POST'

const profileReducer = (state, active) => {
    if (active.type === addPost) {
        let newPost = {
            id: 4,
            message: state.newPostText,
            like: 0}
        //state.postData.push(newPost);
        // state.newPostText = ('');   
    } else if (active.type === updatePost) {
        state.newPostText = active.updateData;
    return state
}}

export const addPostCreate = () => ({type: addPost})
export const updatePostCreate = (text) => ({
    type: updatePost, updateData: text})

export default profileReducer
