import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import { updateNewPost, addMessage, addPost } from './Redux/state';
import { BrowserRouter } from 'react-router-dom';

const root = ReactDOM.createRoot(document.getElementById('root'));

export let rerenderEntireTree = (state) => {

  root.render(
    <BrowserRouter>
      <App state={state} addNewPost={addPost}
       updateNewPost={updateNewPost} addNewMessage={addMessage}/>
    </BrowserRouter>
    );}
