const set_auth_user = 'set-auth-user'


let initialState = {
    userId: null,
    login: null,
    email: null,
    isAuth: false
}

const authReducer = (state=initialState, action) => {
    switch (action.type) {
        case set_auth_user: 
            return {...state,
                ...action.authUser,

            }
        default:
            return state
}}

export const setAuthUser = (userId, login, email) => ({
    type: set_auth_user, authUser: {userId, login, email}})

export default authReducer
