                    if (u.id === action.userId) {
                        return {...u, followed: false }}} )}              
        case setUsers:
            return {...state,
               usersData: [...state.usersData, ...action.usersData]     
            }   

        default:
            return state
}}

export const changeOnFollow = (userId) => ({type: follow, userId: userId})
export const changeOnUnfollow = (userId) => ({type: unfollow, userId: userId})
