import React from 'react'
import { addMessageCreate, updateMessageCreate } from '../../Redux/dialogsReducer'
import Dialogs from './Dialogs'


const DialogsContainer = (props) => {
    let state = props.store.getState()

    let onAddMessage = () => {
        props.dispatch(addMessageCreate())}

    let onCheckUpdate = (text) => {
        props.dispatch(updateMessageCreate(text))}

    return(
        <div >
            <Dialogs addMessage={onAddMessage} checkUpdate={onCheckUpdate}
            dialogsData={state.dialogsData}
            messageData={state.dialogsData.messageData}/>
        </div>
    )
}

export default DialogsContainer
