import axios from 'axios' 

export const getUsers = (currentPage, pageSize) => {
    return axios.get("https://social-network.samuraijs.com/api/1.0/users",
    {params: {page: currentPage, 
        count: this.props.pageSize}}, {withCredentials: true})
    }
