const addMessage = 'ADD-MESSAGE'
const updateMessage = 'UPDATE-MESSAGE'

const dialogsReducer = (state, active) => {
    switch (active) {}
    if (active.type === addMessage) {
        let newMessage = {
            id: 3,
            message: state.updateMessage,}
        state.messageData.push(newMessage);
        state.updateMessage = ('');
    } else if (active.type === updateMessage) {
        state.updateMessage = active.message;
        }
    return state
}

export const addMessageCreate = () => ({type: addMessage})

export const updateMessageCreate = (text) => ({
    type: updateMessage, message: text})

export default dialogsReducer
