import React from 'react'
import s from './MyPosts.module.css';
import Post from './post/post.jsx'
import {reduxForm, Field} from 'redux-form'


const MyPosts = (props) => {

  const onSubmit = (formData) => {
    console.log(formData)}
    props.onAddPost(FormData.NewPost)

  let postItems = props.postData
  .map(p => <Post message={p.message} like={p.like} />)

  let newPostEl = React.createRef();

  let addPost = () => {
    props.onAddPost()
  }

  let onPostChange =() => {
     let text = newPostEl.current.value;
     props.onPostChange(text)
}
  return (
    <div className={s.postBlock} onSubmit={props.handleSubmit}>
      <h3>My posts</h3>
      <div>
        <MyPostReduxForm onSubmit={onSubmit}/>
      </div>
      <div className={s.posts} >
        {postItems}
      </div>
    </div>
  )
}

const MyPostsForm = (props) => {
  return (
    <form onSubmit={props.handleSubmit}>
        <div >
            <Field placeholder='New Post' component={"textarea"} name={"NewPost"}/>
        </div>
        <div>
          <button>Add post</button>
        </div>
    </form>
  )
}

const MyPostReduxForm = reduxForm({form: 'myPost'})(MyPostsForm)

export default MyPosts
