const set_auth_user = 'set-auth-user'

let initialState = {
    authData : [
        {id: null},
        {email: null},
        {login: null}
    ]}

const authReducer = (state=initialState, action) => {
    switch (action.type) {
        case set_auth_user: 
            return {...state,
                authData: {...action.authData}}
        default:
            return state
}}

export const setAuthData = (id, email, login) => ({
    type: set_auth_user, authData: {authData: [id, email, login]}})

export default authReducer
