const addPost = 'ADD-POST'
const updatePost = 'UPDATE-POST'

const profileReducer = (state, action) => {
    switch (action.type) {
        case addPost:
            let newPost = {
                id: 4,
                message: state.newPostText,
                like: 0
            }
            state.postData.push(newPost);
            state.newPostText = ('');
            return state 
        case  updatePost:
            state.newPostText = action.updateData;
            return state
        default:
            return state
}}

export default profileReducer
