import React from 'react'
import Header from './Header';
import axios from 'axios';
import {setAuthUser, getAuthData} from '../../Redux/authReducer'
import {connect} from 'react-redux'
import {userAPI} from '../../api/usersAPI'


class HeaderContainer extends React.Component {
  componentDidMount() {
    userAPI.getAuth().then(data => {
        if (data.resultCode === 0) {
        let {id, login, email} = data.data
        this.props.setAuthUser(id, login, email)}
      }
    )

    axios.get("https://social-network.samuraijs.com/api/1.0/profile/26231", {withCredentials: true}).then(
      response => {
        this.props.getAuthData(response.data)
      }
    )
  }

  render () {
    return (
      <div>
        <Header {...this.props} />
      </div>
    )
  }}

let mapStateToProps = (state) => {
  return {    
    isAuth: state.auth.isAuth,
    login: state.auth.login,
    authData: state.auth.authData,
  }
}

export default connect (mapStateToProps, {setAuthUser, getAuthData})(HeaderContainer)
