import React from 'react'
import s from './MyPosts.module.css';
import Post from './post/post.jsx'


const MyPosts = (props) => {
  let postItems = props.postData
  .map(p => <Post message={p.message} like={p.like} />)

  let newPostEl = React.createRef();

  let addPost = () => {
    let message = newPostEl.current.value;
    props.addNewPost(message)
  }

  let onPostChange =() => {
    let message = newPostEl.current.value;
    props.updateNewPost(message)    
  }

  return (
    <div className={s.postBlock}>
      <h3>My posts</h3>
      <div>
        <div >
          <textarea onClick={updatePost} ref={newPostEl} value={props.updateData}/>
        </div>
        <div>
          <button onClick={addPost}>Add post</button>
        </div>
      </div>
      <div className={s.posts} >
        {postItems}
      </div>
    </div>
  )
}

export default MyPosts
