import React from 'react'
import axios from 'axios';
import {setAuthUser} from "../../Redux/authReducer"
import {connect} from 'react-redux'
import Header from './Header'


class HeaderContainer extends React.Component {
  componentDidMount() {

    axios.get("https://social-network.samuraijs.com/api/1.0/auth/me", {withCredentials: true}).then(
      response => {
        if (response.data.resultCode === 0) {
        let {id, login, email} = response.data.data
        this.props.setAuthUser(id, login, email)}
      }
    )
  }

  render () {
    return (
      <div>
        <Header {...this.props} />
      </div>
    )
  }}

let mapStateToProps = (state) => {
    return {
      login: state.auth.login,

    }
}


export default connect (mapStateToProps, {setAuthUser})(HeaderContainer)
