import React from 'react';
import s from './Users.module.css';

let user = (props) => {

    let pagesCount = Math.ceil(this.props.totalCount/this.props.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount;i++) {
        pages.push(i);
    }
    let changePages = (p) => {
        this.props.setPage(p);
        axios.get("https://social-network.samuraijs.com/api/1.0/users",
        {params: {page: p, count: this.props.pageSize}}).then(response => {
        this.props.setUsers(response.data.items)
    })
    }

        return <div>
                    <div>
                        {pages.map(p => {
                            return <span className={this.props.currentPage === p && s.selected}
                             onClick={() => {changePages(p)}}>{p}</span>
                    })}
                    </div>
                    {this.props.usersData.map(u => <div key={u.id} className={s.users}>
                    <div>
                        <div className={s.photo}>
                            <img  src={u.photos.small != null ? u.photos.small : userPhoto }></img>
                        </div>
                        <div>
                            { u.followed 
                            ? <button onClick={() => {this.props.unfollow(u.id)}}>unfollow</button>
                            : <button onClick={() => {this.props.follow(u.id)}}>follow</button>}   

                        </div>
                    </div>
                    <div className={s.rightSide}>
                        <div>   
                            <div>{u.name}</div>
                            <div>{u.status}</div>
                        </div>
                        <div>
                            <div>{"u.location.cityName"}</div>
                            <div>{"u.location.country"}</div>
                        </div>
                    </div>
                </div>
                )}

                </div>}
