const follow = 'FOLLOW'
const unfollow = 'UNFOLLOW'
const setUsers = 'set-users'

let initialState = {
    usersData: [],
    totalCount: 100,
    pageSize: 20,
    currentPage: 3
};

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case follow:
            return {...state, usersData: state.usersData.map( u => {
                if (u.id === action.userId) {
                    return {...u, followed: true}
                }
                return u
            })}

        case unfollow:
            return {...state, usersData: state.usersData.map(u => {
                if (u.id === action.userId) {
                    return {...u, followed: false}
                }
                return u
            })
            }
        case setUsers:
            return {...state, 
                usersData: [...state.usersData, ...action.users]}

        default:
            return state
}}

export const changeOnFollow = (userId) => ({type: follow, userId})
export const changeOnUnfollow = (userId) => ({type: unfollow, userId})
export const setUsersList = (users) => ({type: setUsers, users})

export default usersReducer
