import React from 'react'
import { addPostCreate, updatePostCreate } from '../../../Redux/profileReducer';
import MyPosts from './MyPosts';
import s from './MyPosts.module.css';

let MyPostsContainer = (props) =>{
  let state = props.getState()

  let onAddPost = () => {
    props.dispatch(addPostCreate())
  }

  let onPostChange =(text) => {
    props.dispatch(updatePostCreate(text))    
  }

  return  (
    <div>
      <MyPosts onPostChange={onPostChange} onAddPost={onAddPost}
      postData={state.profilePage.postData}
      newPostText={state.profilePage.newPostText}/>
    </div>
  )}

export default MyPostsContainer
