let rerenderEntireTree = () => {
    console.log('state changed')
}

let state = {
    profilePage: {
        postData: [
            { id: 1, message: "Hi, how are you?", like: 5 },
            { id: 2, message: "It's my first post", like: 6 },
            { id: 3, message: "and my second post", like: 10 }
        ],
        newPostText: ''
    },
    dialogsPage: {
        messageData : [
            { id: 0, message: 'Hello' },
            { id: 1, message: 'How are you' },
            { id: 2, message: 'Yo' }
        ],
        updateMessage: 'New message',

        dialogsData : [
            { id: 0, name: "Sergey" },
            { id: 1, name: "Andrey" },
            { id: 2, name: "Grisha" },
            { id: 3, name: "Masha" },
            { id: 4, name: "Dasha" }
        ]
    },
    navPage: {
        friendsData: [
            {id: 0, name: 'Andrew', img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRXpnFKklZBeoDxYhmM63xKCzvnAwhfckERUmrIsYvmHg&s'},
            {id: 1, name: 'Sasha', img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT5JXIIGMkVpEvAVwnzWCWeQg91CxY8YG1p-G1x78E5&s'},
            {id: 2, name: 'Pasha', img: 'https://avatars.mds.yandex.net/get-kinopoisk-image/4303601/85c9204f-169c-46fd-8ca7-7473134c450b/1920x'}
        ]
    }  
}

export const addPost = () => {
    let newPost = {
        id: 4,
        message: state.profilePage.newPostText,
        like: 0
    }
    state.profilePage.postData.push(newPost);
    rerenderEntireTree();
    state.profilePage.newPostText = ('');
}

export const addMessage = () => {
    let newMessage = {
        id: 3,
        message: state.dialogsPage.updateMessage,
    }
    state.dialogsPage.messageData.push(newMessage);
    rerenderEntireTree();
    updateNewMessage('')
}

export const updateNewPost = (updateData) => {
    state.profilePage.newPostText = updateData;
    rerenderEntireTree(); 
    }

export const updateNewMessage = (message) => {
    state.dialogsPage.updateMessage = message;
    rerenderEntireTree(); 
}

