const set_Auth_User = 'SET-AUTH-USER'

let initialState = {
    userId: null,
    login: null,
    email: null,
    isAuth: false,
}

const authReducer = (state=initialState, action) => {
    switch (action.type) {
        case set_Auth_User: 
            return {...state,
                ...action.data,
            isAuth: true}
        debugger
        default:
            return state
}}

export const setAuthUser = (userId, login, email) => ({
    type: set_Auth_User, data: {userId, login, email}})

export default authReducer
