import React from 'react'
import { addPostCreate, updatePostCreate } from '../../../Redux/profileReducer';
import StoreContext from '../../../StoreContext';
import MyPosts from './MyPosts';

let MyPostsContainer = () =>{

  let state = store.getState()

  let onAddPost = () => {
    store.dispatch(addPostCreate())}

  let onPostChange =(text) => {
    store.dispatch(updatePostCreate(text))} 

  return  (
    <StoreContext.Consumer> 
      { store => {


        return <MyPosts onPostChange={onPostChange} onAddPost={onAddPost}
                    postData={state.profilePage.postData}
                    newPostText={state.profilePage.newPostText}/>

}} 
    </StoreContext.Consumer>
  )  
  }

export default MyPostsContainer
