import {authAPI} from "../api/usersAPI"

const set_Auth_User = 'SET-AUTH-USER'
const get_Auth_Date = 'GET-AUTH-DATA'


let initialState = {
    userId: null,
    login: null,
    email: null,
    isAuth: false,
    authData: null
}

const authReducer = (state=initialState, action) => {
    switch (action.type) {
        case set_Auth_User: 
            return {
                ...state,
                ...action.payload,
                isAuth: true}
        case get_Auth_Date : 
        return {
            ...state,
            authData: {...action.authData}}
        default:
            return state
}}

export const setAuthUser = (userId, login, email) => ({
    type: set_Auth_User, payload: {userId, login, email}})



export const auth =() => {
    return (dispatch) => {
        authAPI.getAuth().then(data => {
            if (data.resultCode === 0) {
            let {id, login, email} = data.data
            dispatch(setAuthUser(id, login, email))}
          })}
}

export const login = (email, password, rememberMe) => {
    return (dispatch) => {
        authAPI.getLogin(email, password, rememberMe).then(data => {
            if (data.resultCode === 0) {
            dispatch(auth())
            }
          })}
}

export const logout = () => {
    return (dispatch) => {
        authAPI.getLogout().then(data => {
            if (data.resultCode === 0) {
            dispatch(auth())
            }
          })}
}



export default authReducer
