import s from './Dialogs.module.css'
import {NavLink} from 'react-router-dom'

const Item = (props) => {
    return (
        <div className={s.dialog + " " + s.active}>
            <NavLink to ={"/dialogs/"+props.id}>{props.name}</NavLink>
        </div>     
    )
}

const Message = (props) => {
    return (
        <div className={s.dialog}>{props.message}</div>
    )
}
let dialogsData = [
    {id: 0, name: "Sergey"},
    {id: 1, name: "Andrey"},
    {id: 2, name: "Grisha"},
    {id: 3, name: "Masha"},
    {id: 4, name: "Dasha"},
]

let messageData = [
    {id: 0, message: 'Hello'},
    {id: 1, message: 'How are you'},
    {id: 2, message: 'Yo'},
]

let dialogsItems = dialogsData
    .map(mes => <Item name={dialogData.name} id={dialogData.id} /> )

const Dialogs = (props) => {
    return(
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogsItems}
                {/* <Item name={DialogData[0].name} id={DialogData[0].id} />
