import React from 'react'
import { follow, unfollow, setUsers, setPage, setTotal, toggleFollowingProgress} from '../../Redux/usersReducer'
import Users from './Users'
import {connect} from 'react-redux'
import {userAPI }from '../../api/usersAPI'
import axios from 'axios'


class UsersAPI extends React.Component {

    componentDidMount() {
        userAPI.getUsers(this.props.currentPage, this.props.pageSize).then(data => {
                this.props.setUsers(data.items);
                this.props.setTotal(data.totalCount)
        })    
}
    changePages = (p) => {
        this.props.setPage(p);
        axios.get('https://social-network.samuraijs.com/api/1.0/users',
            {params: {page: p, count: this.props.pageSize}}).then(response => {
        this.props.setUsers(response.data.items)
        })}


    render() {
        return (
        <div>
            <Users {...this.props} changePages/>
        </div>)
    }}

let mapStateToProps = (state) => {
    return {
        usersData: state.usersPage.usersData,
        totalCount: state.usersPage.totalCount,
        pageSize: state.usersPage.pageSize,
        currentPage: state.usersPage.currentPage, 
        followingInProgress: state.usersPage.followingInProgress

    }}

const UsersContainer = connect(mapStateToProps,{
    follow, unfollow, setUsers, setPage, setTotal, toggleFollowingProgress})
    (UsersAPI);

export default UsersContainer
