import s from './FormsControlsModule.css'

export const TextArea = ({input, meta, ...props}) => {

    return (
        <div className={s.formControl}>
            <div>
                <textarea {...input} {...props}/>
            </div>
            {meta.touched && meta.error && <span>""</span>}
        </div>
    )


}
