import React from 'react';
import s from './Users.module.css';
import * as axios from 'axios';

let Users = (props) => {
    if (props.usersData.length === 0) {

        axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response => {
            debugger;    
            props.setUsers()
        });

        props.setUsers([
            { id: 1, img:"https://media.vanityfair.com/photos/5e2e3af23f3d910008fc55c0/master/pass/remembering-kobe-bryant.jpg",
            followed: false,  name: "Dmitri", status: "I am locking for a job", location: {cityName: "Minsk", country: "Belarus"} },
            { id: 2, img:"https://media.vanityfair.com/photos/5e2e3af23f3d910008fc55c0/master/pass/remembering-kobe-bryant.jpg",
            followed: true,  name: "Andrew", status: "I am study React", location: {cityName: "Moscow", country: "Russia"} },
            { id: 3, img:"https://media.vanityfair.com/photos/5e2e3af23f3d910008fc55c0/master/pass/remembering-kobe-bryant.jpg",
            followed: false,  name: "Victor", status: "I am student", location: {cityName: "Kiev", country: "Ukraine"} },
        ])}

    return <div>
        {props.usersData.map(u => <div key={u.id} className={s.users}>
            <div>
                <div className={s.photo}>
                    <img  src={u.img}></img>
                </div>
                <div>
                    { u.followed 
                    ? <button onClick={() => {props.unfollow(u.id)}}>unfollow</button>
                    : <button onClick={() => {props.follow(u.id)}}>follow</button>}   

                </div>
            </div>
            <div className={s.rightSide}>
                <div>   
                    <div>{u.name}</div>
                    <div>{u.status}</div>
                </div>
                <div>
                    <div>{u.location.cityName}</div>
                    <div>{u.location.country}</div>
                </div>
            </div>
        </div>
        )}

        </div>}

export default Users
