const addPost = 'ADD-POST'
const updatePost = 'UPDATE-POST'

let initialState ={
    postData: [
        { id: 1, message: "Hi, how are you?", like: 5 },
        { id: 2, message: "It's my first post", like: 6 },
        { id: 3, message: "and my second post", like: 10 }
    ],
    newPostText: ''
}

const profileReducer = (state=ini, active) => {
    switch (active.type) {
        case addPost:
            let newPost = {
                id: 4,
                message: state.newPostText,
                like: 0}
            state.postData.push(newPost);
            state.newPostText = ('');
            return state
        case updatePost:  
            state.newPostText = active.updateData
            return state
        default:
            return state
}}

export const addPostCreate = () => ({type: addPost})
export const updatePostCreate = (text) => ({
    type: updatePost, updateData: text})

export default profileReducer
